$(document).ready(function(){$(".banner-slider").slick({autoplay:true,arrows:false,dots:true});$(".list-gallery").slick({centerMode:true,slidesToShow:3,autoplay:false,arrows:true,dots:false,variableWidth:true});$(".list-categories").slick({slidesToShow:3,autoplay:false,arrows:true,dots:false,responsive:[{breakpoint:767,settings:{slidesToShow:1}}]});$(".select-language .select").select2({minimumResultsForSearch:Infinity,templateSelection:formatState,templateResult:formatState,dropdownParent:$(".dropdown-language")});if($(window).width()<767){$(".main-navigation .navigation-list .item").on("click",function(e){$(this).find(".sub-nav").stop().slideToggle(500)})}if($(window).width()>767){$(".main-navigation .navigation-list .item").on("mouseover",function(e){if($(this).find(".sub-nav").length){$(".main-container").addClass("menu-hover")}});$(".main-navigation .navigation-list .item").on("mouseout",function(e){if($(this).find(".sub-nav").length){$(".main-container").removeClass("menu-hover")}})}$("#bt-open-menu, #btn-close-menu").on("click",function(e){$(".main-navigation").toggleClass("is-opend")});$(".gallery-slider .slider-item .item").magnificPopup({type:"image",mainClass:"mfp-with-zoom",gallery:{enabled:true},zoom:{enabled:true,duration:300,easing:"ease-in-out",opener:function(e){return e.is("img")?e:e.find("img")}}})});function formatState(e){if(!e.id){return e.text}var t=$('<span class="flag-content"><img src="images/'+e.element.value.toLowerCase()+'.png" class="img-flag" /> '+e.text+"</span>");return t}